import React from "react";
import { useForm } from "react-hook-form";
import "../asset/style/Log_in.css";
import Swal from 'sweetalert2'
import { useNavigate } from "react-router-dom";
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from '../firebase.js';

function Login() {
    const navigate = useNavigate();

    const { handleSubmit, register, formState: { errors } } = useForm();

    const onSubmit = values => {
        const { email, password } = values;

        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                // Signed in 
                var user = userCredential.user;
                Swal.fire(
                    'Bienvenido!',
                    '' + user.email,
                    'success'
                )
                navigate('/Index')
            })
            .catch((error) => {
                Swal.fire(
                    'Error!',
                    '' + error.message,
                    'error'
                )
            });
    }


    return (
        <div className="container w-75 mt-5 bg-light rounder shadow">
            <div className="row align-items-stretch">
                <div className="col bg d-none d-lg-block col-md-5 col-lg-7 col-xl-6 rounder">

                </div>


                <div className="col p-5 rounder-end">
                    <div className="text-end">
                        <h3>Fdez</h3>
                    </div>
                    <h2 className="fw-bold text-center py-5">Bienvenido</h2>

                    <form className="was-validated" noValidate onSubmit={handleSubmit(onSubmit)}>
                        <div className="mb-4">
                            <label className="form-label">Escribe Tu Correo</label>
                            <input type="text" className="form-control" id="email" placeholder="Email" required {...register("email", {
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "Invalido email"
                                }
                            })}></input>
                            {errors.email && <span className="text-danger">{errors.email.message}</span>}
                        </div>
                        <div className="mb-4">
                            <label className="form-label">Escribe Tu ConstraseÃ±a</label>
                            <input className="form-control" placeholder="Password" required {...register("password", {
                                required: {
                                    value: true,
                                    message: "El campo requerido",
                                },
                                minLength: {
                                    value: 8,
                                    message: "La contraseÃ±a debe tener minimo 8 caracteres"
                                }
                            })}></input>
                            {errors.password && <span className="text-danger">{errors.password.message}</span>}
                        </div>
                        <div className="my-3">
                            <span><a href="./Recoverpass">Forgot password?</a></span>
                        </div>
                        <div className="d-grid">
                            <button type="submit" className="btn btn-primary">Iniciar Sesion</button>
                        </div>

                        <div className="my-3">
                            <span className="fw-bold">No tienes Cuenta? <a href="./Signup">Create account</a></span>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
